+import java.util.Locale;
+import java.util.ResourceBundle;
+
+public class Controller implements Initializable {
+
+    @FXML
+    public TextArea textArea;
+    @FXML
+    public TextField textField;
+    @FXML
+    public HBox authPanel;
+    @FXML
+    public TextField loginField;
+    @FXML
+    public PasswordField passwordField;
+    @FXML
+    public HBox msgPanel;
+
+    private final String IP_ADDRESS = "localhost";
+    private final int PORT = 8189;
+
+
+    private Socket socket;
+    DataInputStream in;
+    DataOutputStream out;
+
+    private boolean authenticated;
+    private String nickName;
+    private final String TITLE = "VeChat";
+
+    public void setAuthenticated(boolean authenticated) {
+        this.authenticated = authenticated;
+        authPanel.setVisible(!authenticated);
+        authPanel.setManaged(!authenticated);
+        msgPanel.setVisible(authenticated);
+        msgPanel.setManaged(authenticated);
+
+        if (!authenticated) {
+            nickName = "";
+        }
+        setTitle(nickName);
+    }
+
+    @Override
+    public void initialize(URL location, ResourceBundle resources) {
+        setAuthenticated(false);
+    }
+
+
+    public void sendMsg(ActionEvent actionEvent) {
+        try {
+            out.writeUTF(textField.getText());
+            textField.clear();
+            textField.requestFocus();
+        } catch (IOException e) {
+            e.printStackTrace();
+        }
+    }
+
+    public void tryToAuth(ActionEvent actionEvent) {
+        if (socket == null || socket.isClosed()) {
+            connect();
+        }
+        try {
+            out.writeUTF(String.format("/auth %s %s", loginField.getText().trim().toLowerCase(),
+                    passwordField.getText().trim()));
+            passwordField.clear();
+        } catch (IOException e) {
+            e.printStackTrace();
+        }
+
+    }
+
+    private void connect() {
+        try {
+            socket = new Socket(IP_ADDRESS, PORT);
+            in = new DataInputStream(socket.getInputStream());
+            out = new DataOutputStream(socket.getOutputStream());
+
+            new Thread(new Runnable() {
+                @Override
+                public void run() {
+                    try {
+                        // цикл аутентификации
+                        while (true) {
+                            String str = in.readUTF();
+                            if (str.startsWith("/authok")) {
+                                nickName = str.split(" ", 2)[1];
+                                setAuthenticated(true);
+                                break;
+                            }
+                            textArea.appendText(str + "\n");
+                        }
+
+                        // цикл работы
+                        while (true) {
+                            String str = in.readUTF();
+                            if (str.equals("/end")) {
+                                break;
+                            }
+                            textArea.appendText(str + "\n");
+                        }
+                    } catch (IOException e) {
+                        e.printStackTrace();
+                    } finally {
+                        System.out.println("Мы отключились от сервера");
+                        setAuthenticated(false);
+                        try {
+                            socket.close();
+                        } catch (IOException e) {
+                            e.printStackTrace();
+                        }
+                    }
+                }
+            }).start();
+
+        } catch (IOException e) {
+            e.printStackTrace();
+        }
+    }
+
+    private void setTitle(String nick) {
+        Platform.runLater(() -> {
+            ((Stage) textField.getScene().getWindow()).setTitle(TITLE + " " + nick);
+        });
+    }
+}
