+				~errorCode = -1 ; "Unknown error" ;
+				~errorCode = 1 ; "User canceled action" ;
+				~errorCode = 2 ; "Memory error" ;
+				~errorCode = 3 ; "Command is unavailable (for example, wrong operating system, wrong mode, etc.)" ;
