+package ru.gb.lesson_OOP_three;
+
+public class Plate {
+    private int food;
+
+    public Plate(int food) {
+        this.food = food;
+    }
+    public void decreaseFood(int n,String name) {
+
+        if (food>=n) {
+            System.out.println("Омномном, котик **"+ name+"** скушал еды->"+n);
+            food -= n;
+        }
+        else  {
+            System.out.println("Еды следующему котику не хватит, надо бы досыпать до полного ->" +(100-food));
+            food += (100-food);
+            System.out.println("Омномном, котик **"+ name+"** скушал еды->"+n);
+            food -= n;
+        }
+    }
+
+    public void info() {
+        System.out.println("Еды в миске осталось-> " + food);
+    }
+}

