+
+        drawerLayout = v.findViewById(R.id.drawer_layout);
+        ActionBarDrawerToggle toggle = new ActionBarDrawerToggle(getActivity(), drawerLayout,
+                toolbar, R.string.nav_drawer_open, R.string.nav_drawer_close);
