+            SettingsStorage ss = new SettingsStorage();
+            myNote = (MyNote) getArguments().getParcelable(ss.getMyNoteDataToEdit());
+
+            editDescription.setText(myNote.getNoteDescription());
+            editTheme.setText(myNote.getTheme());
+
+        }
+
