+        BigInteger[] f = new BigInteger[n + 2];
+
+        f[0] = BigInteger.ZERO;
+        f[1] = BigInteger.ONE;
+
+        for(int i = 2; i <= n; i++) {
+            f[i] = f[i - 1].add(f[i - 2]);
+        }
+        return f[n];
+    }
