+
+        Map<Character, Integer> count = new HashMap<>();
+
+        for (Character character : s.toCharArray()) {
+            if (count.containsKey(character)) {
+                count.put(character, count.get(character) + 1);
+            }
+            else {
+                count.put(character, 1);
+            }
+        }
+
+        PriorityQueue<Node> priorityQueue = new PriorityQueue<>();
+
+        for (Map.Entry<Character, Integer> entry : count.entrySet()) {
+            priorityQueue.add(new LeafNode(entry.getValue(), entry.getKey()));
+        }
+
+        while(priorityQueue.size() > 1) {
+            priorityQueue.add(new InternalNode(priorityQueue.poll(), priorityQueue.poll()));
+        }
+
+        priorityQueue.poll().fillCodes("");
+
+        StringBuilder sb = new StringBuilder();
+        for (Character character : s.toCharArray()) {
+            sb.append(codes.get(character));
+        }
+
+        return sb.toString();
+        //01001100100111
+        //01001100100111
+    }
+    //!!!!!!!!!!!!!!!!!!!!!!!!!     КОНЕЦ ЗАДАЧИ     !!!!!!!!!!!!!!!!!!!!!!!!!
+
+
+    public static void main(String[] args) throws FileNotFoundException {
+        String root = System.getProperty("user.dir") + "/src/";
+        File f = new File(root + "by/it/a_khmelev/lesson03/dataHuffman.txt");
+        A_Huffman instance = new A_Huffman();
