+public class Cat extends Animal implements Movable {
+
+    private int runDist;
+    private double jumpHeight;
+
+    public Cat(int runDist, double jumpHeight) {
+        this.runDist = runDist;
+        this.jumpHeight = jumpHeight;
+    }
+
+    @Override
+    public boolean run (int lengh) {
+        if (lengh <= runDist){
+            System.out.println("Кот успешно пробежал " + lengh);
+            return true;
+        } else{
+            System.out.println("Кот не смог пробежать " + lengh);
+            return false;
+        }
+    }
+
+    @Override
+    public boolean jump(int height) {
+        if (height <= jumpHeight){
+            System.out.println("Кот успешно перепрыенул " + height);
+            return true;
+        }
+        else {
+            System.out.println("Кот не смог перепрыгнуть " + height);
+            return false;
+        }
+    }
+}

