+
+		switch (selectBy.toUpperCase()) {
+		case "VALUE":
+			oselect.selectByValue(inputValue);
+			break;
+		case "INDEX":
+			int index = Integer.parseInt(inputValue);
+			oselect.selectByIndex(index);
