-  }
-
+    @Override
+    public void explore(File rootDirectory, IFileVisitor vistor) throws IOException {
+        File[] paths = rootDirectory.listFiles();
+        vistor.visit(rootDirectory);
+        if (paths != null) {
+            //on trie la liste car listFiles ne garantit pas l'ordre
+            Arrays.sort(paths);
+            for (File path : paths) {
+                if (path.isDirectory()) {
+                    explore(path, vistor);
+                } else {
+                    vistor.visit(path);
+                }
+            }
+        }
+    }
 }

