+
+    @Override
+    public Link getLink() {
+        if (StringUtils.isNotEmpty(link.get().getURL())) {
+            return link.get();
+        }
+        return null;
+    }
+
+    @Override
+    @JsonIgnore
+    @Deprecated
+    public String getLinkURL() {
+        return super.getLinkURL();
+    }
+
+}

