+package ru.pronichev.lesson1;
+
+public class HomeWork1 {
+    public static void main(String[] args) {
+        System.out.println(method1(1, 2, 3, 4));
+
+        System.out.println(method2(10, 2));
+
+        System.out.println(method3(0));
+
+        System.out.println(method4Print("Вася"));
+
+        System.out.println(method5Year(500));
+    }
+
+    private static double method1(int a, int b, int c, int d) {
+        return a * (b + (c / (double) d));
+    }
+
+    private static boolean method2(int a, int b) {
+        int sum = a + b;
+        return sum >= 10 && sum <= 20;
+    }
+
+    private static boolean method3(int i) {
+        return i >= 0;
+    }
+
+    private static String method4Print(String name) {
+        return "Привет, " + name;
+    }
+
+    private static boolean method5Year(int year) {
+        boolean sup = false;
+        if (year % 4 == 0) {
+            sup = true;
+        }
+        if (year % 100 == 0) {
+            sup = false;
+        }
+        if (year % 400 == 0) {
+            sup = true;
+        }
+        return sup;
+    }
+}
