+        else {
+            for(Product p: cart){
+                System.out.println(p.getId() + " " + p.getName() + " " + p.getPrice());
+            }
+        }
+    }
+
+    public void addToCart(Product product) {
+        cart.add(product);
+    }
+
+    public void removeFromCart(Product product) {
+        cart.remove(product);
+    }
