+package cgeo.geocaching.connector.lc;
+
+import cgeo.geocaching.CgeoApplication;
+import cgeo.geocaching.R;
+import cgeo.geocaching.connector.AbstractLogin;
+import cgeo.geocaching.enumerations.StatusCode;
