+a:visited {
+  text-decoration: underline;
+}
+
+a[href]:after {
+  content: " (" attr(href) ")";
+}
+
+abbr[title]:after {
+  content: " (" attr(title) ")";
+}
+
+/*
+ * Don't show links for images, or javascript/internal links, or header links
+ */
+
+header a:after,
+.ir a:after,
+a[href^="javascript:"]:after,
