+public class Main {
+
+
+        private volatile char Letter='A';
+        private final Object monitor = new Object();
+
+        public static void main(String[] args) {
+
+            Main m = new Main();
+
+            Thread t1 = new Thread(() -> {
+                m.letterA();
+            });
+
+            Thread t2 = new Thread(() -> {
+                m.letterB();
+            });
+
+            Thread t3 = new Thread(() -> {
+                m.letterC();
+            });
+            t1.start();
+            t2.start();
+            t3.start();
+
+        }
+
+        public void letterA() {
+            try {
+                synchronized (monitor) {
+                    for (int i = 0; i < 5; i++) {
+                        while (Letter != 'A') {
+                            monitor.wait(1);
+                        }
+                        System.out.println("A");
+                        Letter = 'B';
+                        monitor.notify();
+                    }
+                }
+            } catch (InterruptedException e) {
+                e.printStackTrace();
+            }}
+        public void letterB () {
+            try {
+                synchronized (monitor) {
+                    for (int i = 0; i < 5; i++) {
+                        while (Letter != 'B') {
+                            monitor.wait(1);
+                        }
+                        System.out.println("B");
+                        Letter = 'C';
+                        monitor.notify();
+                    }
+                }
+            } catch (InterruptedException e) {
+                e.printStackTrace();
+            }
+        }
+        public void letterC () {
+            try {
+                synchronized (monitor) {
+                    for (int i = 0; i < 5; i++) {
