+package com.example.calculatorjm.domain;
+
+public class CalcImplementation implements Calculator {
+
+    @Override
+    public Double BinaryAction(Double firstArgument, Double secondArgument, CalcOperation calcOperation) {
+        Double actionResult = 0.0;
+        switch (calcOperation) {
+            case ADD:
+                actionResult = firstArgument + secondArgument;
+                break;
+            case SUB:
+                actionResult = firstArgument - secondArgument;
+                break;
+            case MUL:
+                actionResult = firstArgument * secondArgument;
+                break;
+            case DIV:
+                if (secondArgument == 0) {
+                    throw new RuntimeException("Division by zero");
+                }
+                actionResult = firstArgument / secondArgument;
+                break;
+            default:
+                break;
+        }
+        return actionResult;
+    }
+}

