+package com.example.calculatorjm.ui;
+
+import android.annotation.SuppressLint;
+import android.widget.Button;
+import android.widget.TextView;
+
+import com.example.calculatorjm.MainActivity;
+import com.example.calculatorjm.domain.CalcImplementation;
+import com.example.calculatorjm.domain.CalcOperation;
+import com.example.calculatorjm.domain.Calculator;
+
+import java.util.ArrayList;
+import java.util.Arrays;
+import java.util.LinkedList;
+import java.util.List;
+
+public class CalcPresenter {
+    private static boolean isSign = true;
+    private static boolean hasPoint = false;
+    private final ArrayList<Double> numbers = new ArrayList<>();
+    private final ArrayList<CalcOperation> operations = new ArrayList<>();
+
+    private final MainActivity view;
+    private TextView calcTextView;
+
+    private final Calculator calculator = new CalcImplementation();
+
+    public void setCalcTextView(TextView calcTextView) {
+        this.calcTextView = calcTextView;
+    }
+
+    public CalcPresenter(MainActivity view) {
+        this.view = view;
+    }
+
+    public void keyNumberPressed(Button button) {
+        displayViewTest(button.getText().toString());
+        isSign = false;
+    }
+
+    public void keyPointPressed() {
+        if (!hasPoint && !isStringEmpty(getCurrentTextView())) {
+            displayViewTest(".");
+            isSign = true;
+            hasPoint = true;
+        }
+    }
+
+    public void keyClearPressed() {
+        this.calcTextView.setText("");
+        isSign = true;
+        hasPoint = false;
+    }
+
+    public void ketDelPressed() {
+        if (!isStringEmpty(getCurrentTextView())) {
+
+            if (isLastWasPoint()) {
+                hasPoint = false;
+            }
+
+            this.calcTextView.setText(getCurrentTextView().subSequence(0,
+                    getCurrentTextView().length() - 1));
+
+            isSign = !isLastDigit();
+        } else {
+            hasPoint = true;
+        }
+    }
+
+    public void keyOperationPressed(String sign, CalcOperation calcOperation) {
+        if (!isSign && !isStringEmpty(getCurrentTextView())) {
+            this.operations.add(calcOperation);
+            displayViewTest(sign);
+            isSign = true;
+            hasPoint = false;
+        }
+    }
+
+    @SuppressLint("SetTextI18n")
+    public void keyEqualPressed() {
+        if (isStringEmpty(getCurrentTextView())) {
+            return;
+        }
+        List<String> tmpArray = new LinkedList<>(Arrays.asList(getCurrentTextView().split("[+\\-*/]")));
+        tmpArray = checkIfFirstElementNull(tmpArray);
+        for (String s : tmpArray) {
+            numbers.add(Double.parseDouble(s));
+        }
+
+        lastOperationRemove();
+
+        while (operations.size() != 0) {
+            if (operations.contains(CalcOperation.MUL) || operations.contains(CalcOperation.DIV)) {
+                for (int i = 0; i < operations.size(); i++) {
+                    if (operations.get(i).equals(CalcOperation.MUL)) {
+                        operationExecution(i);
+                        break;
+                    }
+                    if (operations.get(i).equals(CalcOperation.DIV)) {
+                        operationExecution(i);
+                        break;
+                    }
+                }
+            } else {
+                operationExecution(0);
+            }
+        }
+
+        this.calcTextView.setText(numbers.get(0).toString());
+        this.numbers.clear();
+        this.operations.clear();
+        isSign = false;
+    }
+
+    private List<String> checkIfFirstElementNull(List<String> stringList) {
+        if (isStringEmpty(stringList.get(0))) {
+            stringList.set(0, "-" + stringList.get(1));
+            stringList.remove(1);
+        }
+
+        return stringList;
+    }
+
+    @SuppressLint("SetTextI18n")
+    private void displayViewTest(String s) {
+        this.calcTextView.setText(getCurrentTextView() + s);
+    }
+
+    private String getCurrentTextView() {
+        return this.calcTextView.getText().toString();
+    }
+
+    private void lastOperationRemove() {
+        if (numbers.size() == operations.size()) {
+            operations.remove(operations.size() - 1);
+        }
+    }
+
+    private void operationExecution(int index) {
+        numbers.set(index, calculator.BinaryAction(numbers.get(index), numbers.get(index + 1), operations.get(index)));
+        numbers.remove(index + 1);
+        operations.remove(index);
+    }
+
+    private boolean isStringEmpty(String string) {
+        return string == null || string.isEmpty();
+    }
+
+    private boolean isLastDigit() {
+        String textView = getCurrentTextView();
+        if (isStringEmpty(textView)) {
+            return true;
+        }
+        try {
+            Integer.parseInt(toString().valueOf(getLastCharOfTextView()));
+            return true;
+        } catch (RuntimeException e) {
+            return false;
+        }
+    }
+
+    private boolean isLastWasPoint() {
+        return getLastCharOfTextView() == '.';
+    }
+
+    private char getLastCharOfTextView() {
+        String textView = getCurrentTextView();
+        return textView.charAt(textView.length()-1);
+    }
+}
