+import java.util.Scanner;
+
+/* Autor: Sidney.
+  5. Implemente um programa que recebe um número de 1 a 7 e imprime o dia da semana correspondente.*/
+public class BMquestao5{
+  public static void main(String[] args){
+    System.out.println("Insira um numero de 1 a 7, correspondente ao dia da semana");
+    java.util.Scanner dia = new Scanner (System.in);
+    int diaSemana = dia .nextInt();
+    if(diaSemana > 7 || diaSemana < 1){
+      System.out.println("PLS, INSIRA UM NUMERO VALIDO");
+    }else{
+      switch(diaSemana){
+        case 1: System.out.println("Domingo");
+        break;
+        case 2:System.out.println("Segunda-feira");
+        break;
+        case 3: System.out.println("Terça-feira");
+        break;
+        case 4: System.out.println("Quarta-feira");
+        break;
+        case 5: System.out.println("Quinta-feira");
+        break;
+        case 6: System.out.println("Sexta-feira");
+        break;
+        case 7: System.out.println("Sabado");
+      }
+    }
+  }
+}
