+class ContaBancária {
+  String nome;
+  double saldo;
+
+  void abreContaSimples(String nome) {
+    this.nome = nome;
+  }
+
+  void abreContaEspecial(String nome) {
+    this.nome = nome;
+  }
+
+  void deposita(double valor) {
+    this.saldo += valor;
+  }
+
+  double retira(double valor) {
+    this.saldo -= valor;
+    return saldo;
+  }
+
+  void mostraDados() {
+    System.out.print("A conta no nome de "+nome+" está com saldo de R$ " +saldo);
+    if (this.saldo < 0) {
+      System.out.print(" e está negativada");
+    }
+    else
+      System.out.print("\n");
+  }
+}
+
+class BMExercicio03 {
