+public class time_Campeonato {
+  String nome;
+  ArrayList<String> jogadores = new ArrayList();
+  String tecnico;
+  ArrayList<String> patrocinadores = new ArrayList();
+  String nomeCampeonato;
+  String dataProxJogo;
+  String timeRivalProxJogo;
+  String condicao;
+  int pontuacao;
+  ArrayList<String> funcionarios = new ArrayList();
+
+  void setarNome (String nome) {
+    this.nome = nome;
+  }
+
+  void mudarTecnico (String tecnico) {
+    this.tecnico = tecnico;
+  }
+
+  void setarNomeCampeonato (String nomeCampeonato) {
+    this.nomeCampeonato = nomeCampeonato;
+  }
+
+  void alterarDataProx (String dataProxJogo) {
+    this.dataProxJogo = dataProxJogo;
+  }
+  
+  void alterarTimeRivalProxJogo (String timeRivalProxJogo) {
+    this.timeRivalProxJogo = timeRivalProxJogo;
+  }
+
+  void setarCondicao (String condicao) {
+    this.condicao = condicao;
+  }
+
+  void alterarPontuacao (int pontuacao) {
+    this.pontuacao = pontuacao;
+  }
+
+  void inserirJogador (String jogador, String funcao, int numeroCamisa, boolean ativo) {
+    this.jogadores.add("Jogador: "+jogador+", "+funcao+", camisa número "+numeroCamisa+ ". Em atividade: "+ativo+".");
+  }
+
+  void inserirPatrocionador (String patrocinador, boolean ativo) {
+    this.patrocinadores.add("Patrocinador: "+patrocinador+". Em atividade: "+ativo+".");
+  }
+
+  void inserirFuncionario (String funcionario, String cargo, boolean ativo){
+    this.patrocinadores.add("Nome: "+funcionario+ ". Cargo: "+cargo+". Em atividade: "+ativo+".");
+  }  
+
+  void mostraDados () {
+    System.out.println(this.nome + "Técnico: " +this.tecnico);
+    
+    System.out.println("Jogadores: ");
+    for (String jogador : this.jogadores) {
+      System.out.println(jogador);
+    }
+
+    System.out.println("Patrocinadores: ");
+    for (String patrocinador : this.patrocinadores) {
+      System.out.println(patrocinador);
+    }
+
+    System.out.println(this.nomeCampeonato+":\n"+"Pontuação: "+this.pontuacao+"\n"+"Próximo jogo: "+this.nome + " X "+this.timeRivalProxJogo+ " - " +this.dataProxJogo+ " - " +this.condicao);
+
+    System.out.println("Funcionários: ");
+    for (String funcionario : this.funcionarios) {
+      System.out.println(funcionario);
+    }  
+  }
