+                case SCENARIO_VALIDATION_FIELD_REMOVED_CONSTRAINT_ERROR:
+                    return ScenarioSimulationEditorConstants.INSTANCE.scenarioValidationFieldRemovedConstraintError();
+            }
+        }
+        throw new IllegalArgumentException();
+    }
