+import java.util.ArrayList;
+import java.util.Iterator;
+import java.util.List;
+import java.util.Random;
+
+public class Boot {
+
+    private static final List<Cat> catList = new ArrayList<>();
+    private static final List<Cat> hungryCats = new ArrayList<>();
+    private static final int countFood = 50;
+
+    public static void main(String[] args) {
+        run();
+    }
+
+    public static void run(){
+
+        createCats();
+
+        Plate plate = new Plate(countFood);
+        plate.info();
+
+        //кормим котов
+        for (Cat cat: catList) {
+            cat.eat(plate);
+            cat.info();
+            plate.info();
+            if (!cat.isSatiety()){
+                hungryCats.add(cat);
+            }
+        }
+
+        System.out.println();
+
+        //кормим голодных котов, если они есть, подкладывая в миску еду до тех пор, пока все не станут сытыми
+        while (!hungryCats.isEmpty()){
+            System.out.println("Бедные мои котеи голодные, вот вам еще еды");
+            plate.addFood(countFood - plate.getFood());
+            plate.info();
+
+            Iterator<Cat> it = hungryCats.iterator();
+            while (it.hasNext()) {
+                Cat cat = it.next();
+                cat.eat(plate);
+                cat.info();
+                plate.info();
+                if (cat.isSatiety()){
+                    it.remove();
+                }
+            }
+
+        }
+
+
+    }
+
+    public static void createCats(){
+        int countCats = new Random().nextInt(10) + 1;
+        for (int i = 0; i < countCats; i++) {
+            int appetite = new Random().nextInt(30) + 1;
+            Cat cat = new Cat("Кот_" + (i+1), appetite);
+            catList.add(cat);
+        }
+        System.out.println("В этом кошачьем зоопарке " + countCats + " котов");
+    }
+}

