+public class Cat implements Info {
+
+    private final String name;
+    private final int appetite;
+    private boolean satiety;
+
+    public Cat(String name, int appetite) {
+        this.name = name;
+        this.appetite = appetite;
+    }
+
+    public void eat(Plate plate) {
+        System.out.println(name + " желает съесть " + appetite + " еды...");
+
+        if (plate.getFood() < appetite){
+            satiety = false;
+            System.out.println("В миске недостаточно еды, чтобы накормить кота досыта");
+            return;
+        }
+        plate.decreaseFood(appetite);
+        satiety = true;
+        System.out.println("Кот съел " + appetite + " еды");
+    }
+
+    public boolean isSatiety() {
+        return satiety;
+    }
+
+    @Override
+    public void info() {
+        String s;
+        if (satiety) {
+            s = "Кот сыт";
+        } else {
+            s = "Кот голоден";
+        }
+        System.out.println(s);
+    }
+
+}

