+        }
+    }
+
+    @Override
+    public T set(int index, T element) {
+        readWriteLock.writeLock().lock();
+
+        try {
+            return list.set(index, element);
+        } finally {
+            readWriteLock.writeLock().unlock();
+        }
+    }
+
+    @Override
+    public void add(int index, T element) {
+        readWriteLock.writeLock().lock();
+
+        try {
+            list.add(index, element);
+        } finally {
+            readWriteLock.writeLock().unlock();
+        }
+    }
+
+    @Override
