+public class Mailling {
+     public static void sendMail(String recepient) throws Exception {
+        System.out.println("Preparing to send email");
+        Properties properties = new Properties();
+
+        //Enable authentication
+        properties.put("mail.smtp.auth", "true");
+        //Set TLS encryption enabled
+        properties.put("mail.smtp.starttls.enable", "true");
+        //Set SMTP host
+        properties.put("mail.smtp.host", "smtp.gmail.com");
+        //Set smtp port
+        properties.put("mail.smtp.port", "587");
+
+        //Your gmail address
+        String myAccountEmail = "maitressecole13@gmail.com";
+        //Your gmail password
+        String password = "ghadaarbia123456";
+
+        //Create a session with account credentials
+        Session session = Session.getInstance(properties, new Authenticator() {
+            @Override
+            protected PasswordAuthentication getPasswordAuthentication() {
+                return new PasswordAuthentication(myAccountEmail, password);
+            }
+        });
+
+        //Prepare email message
+        Message message = prepareMessage(session, myAccountEmail, recepient);
+
+        //Send mail
+        Transport.send(message);
+        System.out.println("Message sent successfully");
+    }
+
+     
+     private static Message prepareMessage(Session session, String myAccountEmail, String recepient) {
+        try {
+            Message message = new MimeMessage(session);
+            message.setFrom(new InternetAddress(myAccountEmail));
+            message.setRecipient(Message.RecipientType.TO, new InternetAddress(recepient));
+            message.setSubject("Réservation ajoutée!");
+            String htmlCode = "<h1> CinéJamil </h1> <br/> <h2><b>Votre reservation a été enregistrée. "
+                    + "<br>Pour plus d'informations, veuillez vous connecter sur notre application! </b></h2>";
+            message.setContent(htmlCode, "text/html");
+            return message;
+        } catch (Exception ex) {
+            Logger.getLogger(Mailling.class.getName()).log(Level.SEVERE, null, ex);
+        }
+        return null;
+    }
+    
+    
+}

