+package com.mycompany.finalfantasyjava;
+import java.util.concurrent.ThreadLocalRandom;
+
+/**
+ *
+ * @author Ross
+ */
+public class Weapon {
+    private String attack;
+    private Integer damage;
+    
+    public void setAttack(String attack){
+        this.attack = attack;
+    }
+    
+    public Integer getDamage(){
+        Integer damage = 0;
+        
+        if(this.attack.equals("3-6")){
+            //Generates random number between 3-5. We add 1 to the maximum
+            //because the maximum is not inclusive in this code
+            damage = ThreadLocalRandom.current().nextInt(3, 6 + 1);
+        }
+        
+        return damage;
+    }
+}
