+package ru.geekbrains.geekbrainsnotes.edit;
+
+import android.os.Bundle;
+import android.view.LayoutInflater;
+import android.view.View;
+import android.view.ViewGroup;
+
+import androidx.annotation.NonNull;
+import androidx.annotation.Nullable;
+import androidx.fragment.app.Fragment;
+
+import com.google.android.material.button.MaterialButton;
+import com.google.android.material.textfield.TextInputLayout;
+
+import java.text.SimpleDateFormat;
+import java.util.Calendar;
+import java.util.Locale;
+import java.util.UUID;
+
+import ru.geekbrains.geekbrainsnotes.MainActivity;
+import ru.geekbrains.geekbrainsnotes.R;
+import ru.geekbrains.geekbrainsnotes.model.NoteModel;
+
+public class FragmentEditNotes extends Fragment {
+    private TextInputLayout editTextTitle;
+    private TextInputLayout editTextDescription;
+    private MaterialButton buttonSave;
+    public static final String ARG_INDEX = "arg_index";
+    public static final int INDEX_NEW_NOTE = -1;
+
+    public static FragmentEditNotes newInstance(int index) {
+        FragmentEditNotes fragment = new FragmentEditNotes();
+        Bundle bundle = new Bundle();
+        bundle.putInt(ARG_INDEX, index);
+        fragment.setArguments(bundle);
+        return fragment;
+    }
+
+    @Nullable
+    @Override
+    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
+        return inflater.inflate(R.layout.fragment_notes_edit, container, false);
+    }
+
+    @Override
+    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
+        super.onViewCreated(view, savedInstanceState);
+        initViews(view);
+    }
+
+    private void initViews(View view) {
+        editTextTitle = view.findViewById(R.id.text_input_note_title);
+        editTextDescription = view.findViewById(R.id.text_input_note_description);
+        buttonSave = view.findViewById(R.id.button_save_note);
+        buttonSave.setOnClickListener(v -> saveNote());
+    }
+
+    @Override
+    public void onActivityCreated(@Nullable Bundle savedInstanceState) {
+        super.onActivityCreated(savedInstanceState);
+        if (getArguments() != null) {
+            int index = getArguments().getInt(ARG_INDEX);
+            if (index != INDEX_NEW_NOTE) {
+                NoteModel model = MainActivity.NOTES.get(index);
+                String title = model.getTitle();
+                String description = model.getDescription();
+                editTextTitle.getEditText().setText(title);
+                editTextDescription.getEditText().setText(description);
+            }
+        }
+    }
+
+    private void saveNote() {
+        if (getArguments() != null) {
+            String title = editTextTitle.getEditText().getText().toString();
+            String description = editTextDescription.getEditText().getText().toString();
+            SimpleDateFormat df = new SimpleDateFormat("dd.MM.yyyy", Locale.getDefault());
+            String date = df.format(Calendar.getInstance().getTime());
+            int index = getArguments().getInt(ARG_INDEX);
+            if (index != INDEX_NEW_NOTE) {
+                NoteModel oldModel = MainActivity.NOTES.get(index);
+                NoteModel newModel = new NoteModel(oldModel.getId(), title, description, oldModel.getDateOfCreation());
+                MainActivity.NOTES.set(index, newModel);
+            } else {
+                NoteModel newModel = new NoteModel(UUID.randomUUID().toString(), title, description, date);
+                MainActivity.NOTES.add(newModel);
+            }
+            requireActivity().getSupportFragmentManager()
+                    .popBackStack();
+        }
+    }
+}

