+package ru.geekbrains.geekbrainsnotes.main.adapter;
+
+import androidx.annotation.NonNull;
+import androidx.recyclerview.widget.DiffUtil;
+
+import ru.geekbrains.geekbrainsnotes.model.NoteModel;
+
+public class NoteDiffUtilCallback extends DiffUtil.ItemCallback<NoteModel> {
+    @Override
+    public boolean areItemsTheSame(@NonNull NoteModel oldItem, @NonNull NoteModel newItem) {
+        return oldItem.getId().equals(newItem.getId());
+    }
+
+    @Override
+    public boolean areContentsTheSame(@NonNull NoteModel oldItem, @NonNull NoteModel newItem) {
+        return oldItem.getDateOfCreation().equals(newItem.getDateOfCreation()) &&
+                oldItem.getTitle().equals(newItem.getTitle()) &&
+                oldItem.getDescription().equals(newItem.getDescription());
+    }
+}

