+package ru.geekbrains.geekbrainsnotes.main.adapter;
+
+import android.view.View;
+import android.widget.TextView;
+
+import androidx.annotation.NonNull;
+import androidx.recyclerview.widget.RecyclerView;
+
+import ru.geekbrains.geekbrainsnotes.R;
+import ru.geekbrains.geekbrainsnotes.model.NoteModel;
+
+public class NoteViewHolder extends RecyclerView.ViewHolder {
+    private final NoteAdapterCallbacks callbacks;
+    private final TextView textViewTitle;
+    private final TextView textViewDate;
+
+    public NoteViewHolder(@NonNull View itemView, @NonNull NoteAdapterCallbacks callbacks) {
+        super(itemView);
+        this.callbacks = callbacks;
+        textViewTitle = itemView.findViewById(R.id.item_note_title);
+        textViewDate = itemView.findViewById(R.id.item_note_date);
+    }
+
+    public void onBind(NoteModel model) {
+        textViewTitle.setText(model.getTitle());
+        textViewDate.setText(model.getDateOfCreation());
+
+        itemView.setOnClickListener(view -> {
+            if (getAdapterPosition() != RecyclerView.NO_POSITION) {
+                callbacks.onNoteClick(getAdapterPosition());
+            }
+        });
+
+        itemView.setOnLongClickListener(view -> {
+            if (getAdapterPosition() != RecyclerView.NO_POSITION) {
+                callbacks.onNoteLongClick(getAdapterPosition());
+                return true;
+            }
+            return false;
+        });
+    }
+}

