+package com.example.introdactiontoadnroid;
+
+import androidx.appcompat.app.AppCompatActivity;
+import androidx.appcompat.app.AppCompatDelegate;
+
+import android.annotation.SuppressLint;
+import android.content.Context;
+import android.content.SharedPreferences;
+import android.os.Bundle;
+import android.widget.Switch;
+import android.widget.Toast;
+
+import com.google.android.material.switchmaterial.SwitchMaterial;
+/*
+ * @Abubakar Musanipov
+ * */
+public class SettingsActivity extends AppCompatActivity {
+
+
+    private SwitchMaterial mSwitchTheme;
+    SharedPreferences sp;
+    private static final String PREFS = "pref.xml";
+    private static final String PREF_NAME = "theme";
+    @Override
+    protected void onCreate(Bundle savedInstanceState) {
+        super.onCreate(savedInstanceState);
+        setContentView(R.layout.activity_settings);
+        mSwitchTheme = findViewById(R.id.mSwitchTheme);
+        sp = getSharedPreferences(PREFS, Context.MODE_PRIVATE);
+        checkSwitchBtn(sp.getBoolean(PREF_NAME, false));
+
+        mSwitchTheme.setOnCheckedChangeListener((view, isChecked) -> {
+            if(isChecked){
+                AppCompatDelegate.setDefaultNightMode(AppCompatDelegate.MODE_NIGHT_YES);
+                savePref(true);
+            }else {
+                AppCompatDelegate.setDefaultNightMode(AppCompatDelegate.MODE_NIGHT_NO);
+                savePref(false);
+            }
+        });
+    }
+
+    private void checkSwitchBtn(boolean isChecked){
+        if(isChecked){
+            mSwitchTheme.setChecked(true);
+        }else {
+            mSwitchTheme.setChecked(false);
+        }
+    }
+
+    private void savePref(boolean nightTheme){
+        SharedPreferences.Editor editor = sp.edit();
+        editor.putBoolean(PREF_NAME, nightTheme);
+        editor.commit();
+    }
+
+
+}
