+public class NoteListFragment extends Fragment {
+
+    private ArrayList<Note> notes = new ArrayList();
+    private int positionId;
+    private NoteAdapter noteAdapter;
+    private ListView notesList;
+    private Calendar dateAndTime = Calendar.getInstance();
+
+    @Override
+    public void onCreate(Bundle savedInstanceState) {
+        super.onCreate(savedInstanceState);
+    }
+
+    @Override
+    public View onCreateView(LayoutInflater inflater, ViewGroup container,
+                             Bundle savedInstanceState) {
+        ViewGroup view = (ViewGroup) inflater.inflate(R.layout.fragment_notes, container, false);
+        setInitialData();
+        notesList = (ListView) view.findViewById(R.id.list_view);
+        noteAdapter = new NoteAdapter(getActivity(), R.layout.note, notes);
+        notesList.setAdapter(noteAdapter);
+        AdapterView.OnItemClickListener itemClickListener = (AdapterView<?> parent, View v, int position, long id) ->{
+            Note note = (Note) parent.getItemAtPosition(position);
+            if (getResources().getConfiguration().orientation == Configuration.ORIENTATION_PORTRAIT){
+                goToTheNoteStructure(note);
+            }else {
+                showToTheRight(note);
+            }
+        };
+        registerForContextMenu(notesList);
+
+        notesList.setOnItemClickListener(itemClickListener);
+
+        return view;
+    }
+
+    private void goToTheNoteStructure(Note note){
+        Intent intent = new Intent(getActivity(), NoteActivity.class);
+        intent.putExtra(Note.class.getSimpleName(), note);
+        startActivity(intent);
+    }
+    private void showToTheRight(Note note) {
+        FragmentManager fragmentManager = requireActivity().getSupportFragmentManager();
+        FragmentTransaction transaction = fragmentManager.beginTransaction();
+        transaction.replace(R.id.note_container, NoteFragment.newInstance(note));
+        transaction.setTransition(FragmentTransaction.TRANSIT_FRAGMENT_FADE);
+        transaction.commit();
+    }
+
+
+    private void setInitialData(){
+        notes.add(new Note("Task1", "Task description 1", "2020-8-12"));
+        notes.add(new Note("Task2", "Task description 2", "2020-9-1"));
+        notes.add(new Note ("Task3", "Task description 3", "2020-4-6"));
+
+    }
+    public void setDate() {
+        new DatePickerDialog(getActivity(), d,
+                dateAndTime.get(Calendar.YEAR),
+                dateAndTime.get(Calendar.MONTH),
+                dateAndTime.get(Calendar.DAY_OF_MONTH))
+                .show();
+    }
+    DatePickerDialog.OnDateSetListener d = (DatePicker view, int year, int monthOfYear, int dayOfMonth) ->
+    {
+            monthOfYear += 1;
+            String date = year + "-" + monthOfYear  + "-" + dayOfMonth;
+            Note note = notes.get(positionId);
+            note.setDate(date);
+            notes.set(positionId, note);
+            noteAdapter.notifyDataSetChanged();
+
+    };
+
+    @Override
+    public void onCreateContextMenu(@NonNull ContextMenu menu, @NonNull View v, @Nullable ContextMenu.ContextMenuInfo menuInfo) {
+        super.onCreateContextMenu(menu, v, menuInfo);
+        menu.add("Set date");
+    }
+
+    @Override
+    public boolean onContextItemSelected(@NonNull MenuItem item) {
+        if(item.getItemId() == 0){
+            AdapterView.AdapterContextMenuInfo acmi = (AdapterView.AdapterContextMenuInfo) item.getMenuInfo();
+            positionId = (int) acmi.id;
+            setDate();
+        }
+        return super.onContextItemSelected(item);
+    }
+}
