+import androidx.recyclerview.widget.RecyclerView;
+
+import com.example.introdactiontoadnroid.R;
+import com.example.introdactiontoadnroid.adapter.Adapter;
+import com.example.introdactiontoadnroid.model.Note;
+
+import java.util.ArrayList;
+import java.util.List;
+
+
+public class NoteListFragment extends Fragment {
+
+    private List<Note> notes = new ArrayList();
+    private int position;
+    private Note note;
+    private static final String ARG_PARAM1 = "note";
+    private static final String  ARG_PARAM2 = "position";
+
+
+    public static NoteListFragment newInstance(Note param1, int position) {
+        NoteListFragment fragment = new NoteListFragment();
+        Bundle args = new Bundle();
+        args.putSerializable(ARG_PARAM1, param1);
+        args.putInt(ARG_PARAM2, position);
+        fragment.setArguments(args);
+        return fragment;
+    }
+
+
+
+    @Override
+    public void onCreate(@Nullable Bundle savedInstanceState) {
+        super.onCreate(savedInstanceState);
+        setInitialData();
+        if (getArguments() != null) {
+            note = (Note) getArguments().getSerializable(ARG_PARAM1);
+            position = getArguments().getInt(ARG_PARAM2);
+            notes.set(position, note);
+        }
+
+    }
+
+    @Override
+    public View onCreateView(LayoutInflater inflater, ViewGroup container,
+                             Bundle savedInstanceState) {
+        ViewGroup view = (ViewGroup) inflater.inflate(R.layout.fragment_list, container, false);
+        RecyclerView recyclerView = view.findViewById(R.id.recycler_view);
+        Adapter.OnNoteClickListener onNoteClickListener = ((Note note, int position) -> {
+            if (getResources().getConfiguration().orientation == Configuration.ORIENTATION_PORTRAIT){
+                changeFragment(note, position);
+            }else {
+                showToTheRight(note, position);
+            }
+        });
+
+        Adapter adapter = new Adapter(getActivity(), notes, onNoteClickListener);
+        recyclerView.setAdapter(adapter);
+
+        return view;
+    }
+
+
+    private void showToTheRight(Note note, int position) {
+        FragmentManager fragmentManager = requireActivity().getSupportFragmentManager();
+        FragmentTransaction transaction = fragmentManager.beginTransaction();
+        transaction.replace(R.id.note_container, NoteFragment.newInstance(note, position));
+        transaction.setTransition(FragmentTransaction.TRANSIT_FRAGMENT_FADE);
+        transaction.commit();
+    }
+    // Прокоментируйте как можно избежать такого повторения кода
+    private void changeFragment(Note note, int position){
+        FragmentManager fragmentManager = requireActivity().getSupportFragmentManager();
+        FragmentTransaction transaction = fragmentManager.beginTransaction();
+        transaction.setCustomAnimations(R.animator.side_in_left, R.animator.slide_in_right);
+        transaction.replace(R.id.main_container, NoteFragment.newInstance(note, position));
+        transaction.addToBackStack(null);
+        transaction.setTransition(FragmentTransaction.TRANSIT_FRAGMENT_FADE);
+        transaction.commit();
+
+    }
+
+
+    private void setInitialData(){
+        notes.add(new Note("Task1", "Task description 1", "2020-8-12"));
+        notes.add(new Note("Task2", "Task description 2", "2020-9-1"));
+        notes.add(new Note ("Task3", "Task description 3", "2020-4-6"));
+
+    }
+
+
+
+}
